import React from "react";
import '../assets/css/main.css'
import logochip from '../assets/img/logochip.png'
import {endPoint} from '../services/apiRest.js'
import axios from 'axios'

class Login extends React.Component {
    constructor(props){
        super(props);
    }

    state={
        form:{
            "email":"",
            "password":""
    },
    error:false,
    errorMsg:""
}
        
    eventForm=(e)=>{
        e.preventDefault() 


    }
    eventChange= async e=>{
        await this.setState({
            form:{
                ...this.state.form,
                [e.target.name] : e.target.value
            }
        })

    }
    eventSubmit=()=>{
        let url = endPoint + 'api/login-clientes'
        axios.post(url,this.state.form)
        .then(response=>{
            if(response.data.error){
                this.setState({
                    error: true,
                    errorMsg: response.data.mensaje
                    
                })
                return;
            }else{
                // console.log()
                const Access_token = response.data.Access_token.split("|");
                localStorage.setItem('token', Access_token[1])
                // this.props.history.push("/catalogo")
            }
            
        }).catch(
            error=>{
                this.setState({
                    error: true,
                    errorMsg: 'no se pudo validar las credenciales '
                })
            }
        )
 
     }

    render() {
        return(
        <React.Fragment>

            <div className="wrapper fadeInDown">
                <div id="formContent">



                    <div className="fadeIn first">
                        <img src={logochip} width ='100' alt="User Icon" />
                    </div>


                    <form onSubmit={this.eventForm}>
                        <input type="text" name="email" className="fadeIn second"  placeholder="email" onChange={this.eventChange} />
                        <input type="password" name="password" className="fadeIn third" placeholder="password" onChange={this.eventChange} />
                        <input type="submit" className="fadeIn fourth" value="Log In" onClick={this.eventSubmit} />
                    </form>


                    <div id="formFooter">
                        {
                            this.state.error ===true &&
                                <div className="alert alert-danger" role="alert">

                                        {this.state.errorMsg}
                                </div>
                        }
                    </div>

                </div>
            </div>
        </React.Fragment>
        )
    }
}

export default Login
