import React from "react";
import '../assets/css/main.css'
import logochip from '../assets/img/logochip.png'

class Login extends React.Component {

    state={
        form:{
            "login":"",
            "password":""
    },
    error:false,
    errorMsg:""
}
        
eventForm=(e)=>{
    e.preventDefault() 


 }

    eventSubmit=(e)=>{
       e.preventDefault() 


    }

    eventChange= async e=>{
        await this.setState({
            form:{
                ...this.state.form,
                [e.target.name] : e.target.value
            }
        })

    }

    render() {
        return(
        <React.Fragment>

            <div className="wrapper fadeInDown">
                <div id="formContent">



                    <div className="fadeIn first">
                        <img src={logochip} width ='100' alt="User Icon" />
                    </div>


                    <form onSubmit={this.eventForm}>
                        <input type="text" name="login" className="fadeIn second"  placeholder="login" onChange={this.eventChange} />
                        <input type="password" name="password" className="fadeIn third" placeholder="password" onChange={this.eventChange} />
                        <input type="submit" className="fadeIn fourth" value="Log In" />
                    </form>


                    <div id="formFooter">
                        {/* <a className="underlineHover" href="#">Forgot Password?</a> */}
                    </div>

                </div>
            </div>
        </React.Fragment>
        )
    }
}

export default Login
