import axios from 'axios'
import{link} from 'react-router-dom'

const endPoint = 'http://127.0.0.1:8000/';
const ShowProducto = () => {
    const [ products , setProducts ]=useState([])
    useEffect(()=>{
        getAllProducts()
    },[])


    const getAllProducts= async ()=>{
       const response = await axios.get(`${endPoint}api/product-all`)
       setProducts(response.data);
    }
    

  return (

        
        
    <div className="row" > {products.map((e) => (

        <div class="col-sm-2">
    <div className="card-body center" >
        <div className="card" style={{width: "18rem"}}>
        <img className="card-img-top" src={`${endPoint}${e.img}`} alt="Card image cap"></img>
        <div className="card-body">
        <h5 className="card-title">{e.nombre}</h5>
        <p className="card-text">{e.description}</p>
        <a href="#" className="btn btn-primary">enviar</a>
        </div>
        
      </div>
       </div>
       </div>
        ))  }</div>
    

  
    
    

  )
}

export default ShowProducto
